import Post from "../models/posts";
import AccountModel from "../models/accounts";
import { verifyJwtToken } from '../helper/utils';
import UserModel from "../models/users"
import e from "express";

const TRUE_STATUS = ["happy", "angry", "sad"];
// List API used for Post Status
/**
 * @author NinhDang
 * @description add a post after login 
 * @param {Object} req
 * @param {Object} res
 * @returns {string}
 */
const addPost = async (req, res) => {

    const { token, described, status } = req.body;
    if (!token) {
      return res.json({
        code: "1002",
        message: "Parameter is not enough",
      });
    }
    
    if ( 
      typeof token !== "string" ||
      typeof described !== "string" ||
      typeof status !== "string"
    ) {
      res.status(422).json({ message: "Invalid data"})
      return;
    }

    try {
      
      await verifyJwtToken(token, process.env.jwtSecret).then(async () => {
        const truePost = TRUE_STATUS.includes(status) && (described !== "");
        let imageArray = [];
        imageArray.push(req.file.path ||  "");

        if (truePost) {
          const savedPost = new Post({
            image: imageArray ?? '',
            video: [] ?? '',
            described: described ?? '' ,
            status: status ?? '',
            banned: 0,
            likes: [],
          })

          const post = await savedPost.save();

          return res.status(200).json({
            code: "1000",
            message: "Post created successfully!",
            data: {
              id: post.id ?? '',
              url: post.url ?? '',
            },
          }); 
        } else {
          res.status(400).json({
            code: "1001",
            message: "Post created failed! Check parameters!"
          })
        }

      }).catch(err => {
        return res.json({
          code: '1005',
          message: 'expired token, go to login page' + err,
        })
      })
    } catch (err) {
        res.status(500).json({
          code: "9999",
          message: "Server failed to post!" + err,
        });
    }  
}

const deletePost = async (req, res) => {
    try {
        const { token, id } = req.body;
        
        if (!token || !id) {
          return res.json({
            code: "1002",
            message: "Parameter is not enough",
          });
        } else {
          await verifyJwtToken(token, process.env.jwtSecret)
          .then(async () => {

            const user = await AccountModel.findOne({token});
            if (user) {
              if ( user.is_blocked === 1 ) {
                return res.json({
                  code: '1005',
                  message: 'expired token, go to login page' + err,
                })
              }
            }

            const post = await Post.findById(id);
            if (!post) {
              res.status(403).json({
                code: 9992,
                message: "post is not existed"
              });
              return;
            } else {
              if (post.banned === "1" || post.banned === "2") {
                res.json({
                  code: 9992,
                  message: 'the post is banned!'
                });

                await post.deleteOne();
                return;
              } else {
                await post.deleteOne();
                return res.status(200).json({
                  code: 1000,
                  message: "the post has been deleted"
                })
              }
            }   
     
          }).catch(err => {
            return res.json({
              code: '1005',
              message: 'expired token, go to login page' + err,
            });
          });
        }
        
    } catch (err) {
        res.status(500).json({
          code: 1001,
          message: "Your internet is disconnected!"
        });
    }
}

const reportPost = async (req, res) => {
  try {
    const { token, id, subject, details } = req.body;

    const SUBJECT = ["Bo lc", "Kha thn", "Cht cm", "xuyn tc chng ph kch ng chin tranh"];
    const trueReport = SUBJECT.includes(subject);


    if (!(token || id || subject || details)) {
      return res.json({
        code: "1002",
        message: "Parameter is not enough",
      });
    }
    
    if ( 
      typeof token !== "string" ||
      typeof id !== "string" ||
      typeof subject !== "string" ||
      typeof details !== "string"      
    ) {
      res.status(422).json({ message: "Invalid data"})
      return;
    } else {
      await verifyJwtToken(token, process.env.jwtSecret)
          .then(async () => {

            const account = await AccountModel.findOne({token});
            const user = await UserModel.findById(account._id);
            if (user) {
              if (user.is_blocked === 1 ) {
                return res.json({
                  code: '1005',
                  message: "user is banned, go to login page",
                })
              }
            }

            const post = await Post.findById(id);
            if (!post) {
              res.status(403).json({
                code: "9992",
                message: "post is not existed"
              });
              return;
            } else {
              if (post.banned === "1" || post.banned === "2") {
                res.json({
                  code: "1000",
                  message: 'the post is banned!'
                });

                await post.deleteOne();
                return;
              } else {

                console.log(post.id);
                try {
                  await Post.updateOne({_id: id} , {$set: {banned: '1'}});
                } catch (err) {
                  console.log(err);
                }

                return res.status(200).json({
                  code: "1000",
                  message: "the post has reported successfully!"
                })
              }
            }   
     
          }).catch(err => {
            return res.json({
              code: '1005',
              message: 'expired token, go to login page' + err,
            });
          });
    }

    
  } catch (err) {
    res.status(500).json({
      code: 1001,
      message: "your internet is disconnected!"
    });
  }
}

const getPost = async (req, res) => {

  const { token, id } = req.body;
  if (!token || !id ) {
    return res.json({
      code: "1002",
      message: "Parameter is not enough",
    });
  } 

  if ( 
    typeof token !== "string" ||
    typeof id !== "string" 
  ) {
    res.status(422).json({ message: "Invalid data"})
    return;
  } else {
    await verifyJwtToken(token, process.env.jwtSecret)
    .then(async () => {

      const account = await AccountModel.findOne({token});
      const user = await UserModel.findOne({ id: account._id });
      if (user) {
        if (user.is_blocked === 1 ) {
          return res.json({
            code: '1005',
            message: 'user is blocked, go to login page' + err,
          })
        }
      }

      const post = await Post.findById(id);
      if(!post) {
        res.status(403).json({
          code: 9992,
          message: "post is not existed"
        });
      } else {
        if (post.banned === "1" || post.banned === "2") {
          res.json({
            code: 9992,
            message: 'the post is banned!'
          });

          await post.deleteOne();
          return;
        }

        const isLiked = post.likes.includes(user.id) ? "1" : "0";
        
        return res.status(200).json({
          code: "1000",
          message: "get post successfully!",
          data: {
            id: post.id,
            described: post.described,
            created: post.createdAt,
            modified: post.updatedAt,
            like: post.likes.length.toString(),
            comment: post.comments.length.toString(),
            is_like: isLiked,
            image: {
              id: post.image,
              url: ""
            },
            video: {
              id: post.video ?? "",
              url:""
            },
            author: {
              id: user.id,
              name: user.username,
              avatar: user.avatar,
              online: user.online,
            },
            state: "",
            is_blocked: user.is_blocked,
            can_edit: post.can_edit ?? "",
            banned: post.banned,
            can_comment: post.can_comment,
            url: post.url ?? "",
            messages:  post.messages ?? "",
          }
        })
      }

    }).catch(err => {
      return res.json({
        code: '1005',
        message: 'expired token, go to login page' + err,
      });
    });
  }
}


// Comments 
const getComment = async (req, res) => {

    const { token, id, index, count } = req.body;
    if (!token || !id || !index || !count) {
      return res.json({
        code: "1002",
        message: "Parameter is not enough",
      });
    } 

    if ( 
      typeof token !== "string" ||
      typeof id !== "string" ||
      typeof Number(index) !== "number" ||
      typeof Number(count) !== "number" 
    ) {
      res.status(422).json({ message: "Invalid data"})
      return;
    } else {
      await verifyJwtToken(token, process.env.jwtSecret)
      .then(async () => {

        const account = await AccountModel.findOne({token});
        const user = await UserModel.findOne({ id: account._id });
        if (user) {
          if (user.is_blocked === 1 ) {
            return res.json({
              code: '1005',
              message: 'user is blocked, go to login page' + err,
            })
          }
        }

        const post = await Post.findById(id);
        if(!post) {
          res.status(403).json({
            code: 9992,
            message: "post is not existed"
          });
        } else {
          if (post.banned === "1" || post.banned === "2") {
            res.json({
              code: 9992,
              message: 'the post is banned!'
            });

            await post.deleteOne();
            return;
          }

          let limitedPostComments = [];
          
          if (index && count) {
            const start = index * count;
            const end = start + count;
            limitedPostComments = post.comments.slice(start, end);
          }
          
          return res.status(200).json({
            code: "1000",
            message: "get comment successfully!",
            data: limitedPostComments,
            is_blocked: user.is_blocked,
          })
        }

      }).catch(err => {
        return res.json({
          code: '1005',
          message: 'expired token, go to login page' + err,
        });
      });
    }
}

const setComment = async (req, res) => {
  try {
    const { token, id, comment, index, count } = req.body;
    if (!token || !id || !comment || !index || !count) {
      return res.json({
        code: "1002",
        message: "Parameter is not enough",
      });
    } 

    if ( 
      typeof token !== "string" ||
      typeof id !== "string" ||
      typeof comment !== "string" ||
      typeof Number(index) !== "number" ||
      typeof Number(count) !== "number" 
    ) {
      res.status(422).json({ message: "Invalid data"})
      return;
    } else {
      await verifyJwtToken(token, process.env.jwtSecret)
          .then(async () => {

            const account = await AccountModel.findOne({token});
            const user = await UserModel.findOne({ id: account._id });
            if (user) {
              if (user.is_blocked === 1 ) {
                return res.json({
                  code: '1005',
                  message: 'user is blocked, go to login page' + err,
                })
              }
            }

            const post = await Post.findById(id);
            if (!post) {
              res.status(403).json({
                code: 9992,
                message: "post is not existed"
              });
              return;
            } else {
              if (post.banned === "1" || post.banned === "2") {
                res.json({
                  code: 9992,
                  message: 'the post is banned!'
                });

                await post.deleteOne();
                return;
              } else {

                const userId = user._id.toString();
                const username = user.username;
                console.log(username);
                const userAvatar = user.avatar;

                const new_comment = {
                  poster: {
                    id: userId ?? "",
                    name: username ?? "",
                    avatar: userAvatar ?? "",
                  },
                  comment: comment,
                  created: new Date(),
                };
                console.log(new_comment);
                post.comments.push(new_comment);

                try {
                  post.save();
                } catch (err) {
                  console.log(err);
                  res.status(500).json({ msg: err });
                  return;
                }
                
                return res.status(200).json({ 
                  code: "1000",
                  message: "set comment success!",
                  data: {
                    id: post._id || "",
                    comment: post.comments[0].comment,
                    created: post.comments[0].timestamp,
                    poster: {
                      id: post.comments[0].poster.id,
                      name: post.comments[0].poster.name,
                      avatar: post.comments[0].poster.avatar
                    },
                    is_blocked: user.is_blocked ?? "",
                  }
                });

              }
            }  
          }).catch(err => {
            return res.json({
              code: '1005',
              message: 'expired token, go to login page' + err,
            });
          });
        }

  } catch (err) {
    res.status(500).json({
      code: 1001,
      message: "your internet is disconnected!" + err,
    });
  }
}

const like = async (req, res) => {
    const { token, id } = req.body;
    if (!token || !id) {
      return res.json({
        code: "1002",
        message: "Parameter is not enough",
      });
    }

    if ( 
      typeof token !== "string" ||
      typeof id !== "string" 
    ) {
      res.status(422).json({ message: "Invalid data"})
      return;
    } else {
      
      await verifyJwtToken(token, process.env.jwtSecret)
      .then(async () => {
        let postFind = null;
        try {
          postFind = await Post.findById(id);
        } catch (err) {
          res.json({msg: err});
          return;
        }

        if (postFind === null) {
          res.status(422).json({message: "Not found post!"})
          return;
        }

        const userId = await UserModel.findOne({token});
        // Nu likes[] cha userid th unlike, ngc li th like 
        if (userId && postFind.likes.includes(userId.id)) {
          let newLikes = postFind.likes.filter((item) => item !== userId.id);
          postFind.likes = newLikes;
        } else {
          postFind.likes.push(userId.id);
        }
        
        try {
          await postFind.save();

          return res.status(200).json({
              code: "1000",
              message: "like/unlike successfully!",
              data: {
                like: postFind.likes.length,
              }
          });
        } catch {
          res.status(500).json({msg: "Not connect Internet"});
          return;
        }

      }).catch(err => {
        return res.json({
          code: '1005',
          message: 'expired token, go to login page' + err,
        });
      });
  }
}


module.exports = {
    addPost,
    deletePost,
    getPost,
    reportPost,
    like,
    setComment,
    getComment,
}

